mapper.select:
SELECT 

FROM oa_notify a

WHERE a.id = #{id}

SELECT
, r.read_flag
FROM oa_notify a
JOIN oa_notify_record r ON r.oa_notify_id = a.id AND r.user_id = #{currentUser.id}
WHERE a.del_flag = #{DEL_FLAG_NORMAL}
AND a.TITLE LIKE '%'||#{title}||'%' '%'+#{title}+'%' CONCAT('%', #{title}, '%')
AND a.TYPE = #{type}
AND a.STATUS = #{status}
AND a.STATUS = '1'
ORDER BY a.update_date DESC

SELECT 
	count(1)
FROM oa_notify a

	JOIN oa_notify_record r ON r.oa_notify_id = a.id AND r.user_id = #{currentUser.id}

			AND r.read_flag = #{readFlag}


WHERE a.del_flag = #{DEL_FLAG_NORMAL}

	AND a.STATUS = '1'
